mapper.update:

update board
set hit = hit + 1
where boardno = #{boardno}


update board
set btitle =#{btitle}
	, bcontent = #{bcontent}

where boardno = #{boardno}

mapper.select:

select * from (
	select rownum rnum, B.* FROM (
		select
			boardno , btitle , bcontent , id , nick , hit , bdate , cateno 
		from board
		where cateno = 2
		order by boardno DESC
	) B
) BOARD

where rnum between #{startNo } and #{endNo }

mapper.update:
update board
set hit = hit + 1
where boardno = #{boardno}


update board
set btitle =#{btitle}
	, bcontent = #{bcontent}

where boardno = #{boardno}
